          },
          to: {
            height: '0',
          },
        },
      },
      borderRadius: {
        lg: 'var(--radius)',
        md: 'calc(var(--radius) - 2px)',
        sm: 'calc(var(--radius) - 4px)',
      },
    },
  },
  plugins: [require('tailwindcss-animate')],
};

export default config;

app/globals.css.content:
@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --background: 10 10% 4%; /* Dark background */
  --foreground: 0 0% 98%;
  --card: 10 10% 4%;
  --card-foreground: 0 0% 98%;
  --popover: 10 10% 4%;
  --popover-foreground: 0 0% 98%;
  --primary: 0 85% 60%; /* Aura red */
  --primary-foreground: 0 0% 98%;
  --secondary: 0 0% 15%;
  --secondary-foreground: 0 0% 98%;
  --muted: 0 0% 15%;
  --muted-foreground: 0 0% 64%;
  --accent: 0 0% 15%;
  --accent-foreground: 0 0% 98%;
  --destructive: 0 62% 30%;
  --destructive-foreground: 0 0% 98%;
  --border: 0 0% 15%;
  --input: 0 0% 15%;
  --ring: 0 85% 60%;
  --radius: 0.5rem;
}

@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply bg-brand-black text-foreground font-rounded;
    background: linear-gradient(135deg, #0a0a0a 0%, #1b0000 100%);
    min-height: 100vh;
  }

  h1, h2, h3, h4, h5, h6 {
    @apply font-heavy font-bold;
  }
}

@layer components {
  .aura-glow {
    @apply animate-glow;
  }

  .glass-effect {
    @apply backdrop-blur-md bg-white/5 border border-white/10;
  }

  .product-card {
    @apply glass-effect rounded-lg p-4 hover:bg-white/10 transition-all duration-300;
  }

  .btn-primary {
    @apply bg-gradient-to-r from-brand-crimson to-brand-auraRed hover:from-brand-auraRed hover:to-brand-crimson text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300 hover:scale-105;
  }

  .btn-secondary {
    @apply glass-effect hover:bg-white/10 text-white font-semibold py-3 px-6 rounded-lg transition-all duration-300;
  }
}

/* Custom scrollbar */
::-webkit-scrollbar {
  width: 8px;
}

::-webkit-scrollbar-track {
  background: #1b0000;
}

::-webkit-scrollbar-thumb {
  background: #8b0000;
  border-radius: 4px;
}

::-webkit-scrollbar-thumb:hover {
  background: #ff2b4a;
}

