hooks/use-toast.ts.content:
'use client';

// Inspired by react-hot-toast library
import * as React from 'react';

import type { ToastActionElement, ToastProps } from '@/components/ui/toast';

const TOAST_LIMIT = 1;
const TOAST_REMOVE_DELAY = 1000000;

type ToasterToast = ToastProps & {
  id: string;
  title?: React.ReactNode;
  description?: React.ReactNode;
  action?: ToastActionElement;
};

const actionTypes = {
  ADD_TOAST: 'ADD_TOAST',
  UPDATE_TOAST: 'UPDATE_TOAST',
  DISMISS_TOAST: 'DISMISS_TOAST',
  REMOVE_TOAST: 'REMOVE_TOAST',
} as const;

let count = 0;

function genId() {
  count = (count + 1) % Number.MAX_SAFE_INTEGER;
  return count.toString();
}

type ActionType = typeof actionTypes;

type Action =
  | {
      type: ActionType['ADD_TOAST'];
      toast: ToasterToast;
    }
  | {
      type: ActionType['UPDATE_TOAST'];
      toast: Partial<ToasterToast>;
    }
  | {
      type: ActionType['DISMISS_TOAST'];
      toastId?: ToasterToast['id'];
    }
  | {
      type: ActionType['REMOVE_TOAST'];
      toastId?: ToasterToast['id'];
    };

interface State {
  toasts: ToasterToast[];
}

const toastTimeouts = new Map<string, ReturnType<typeof setTimeout>>();

const addToRemoveQueue = (toastId: string) => {
  if (toastTimeouts.has(toastId)) {
    return;
  }

  const timeout = setTimeout(() => {
    toastTimeouts.delete(toastId);
    dispatch({
      type: 'REMOVE_TOAST',
      toastId: toastId,
    });
  }, TOAST_REMOVE_DELAY);

  toastTimeouts.set(toastId, timeout);
};

export const reducer = (state: State, action: Action): State => {
  switch (action.type) {
    case 'ADD_TOAST':
      return {
        ...state,
        toasts: [action.toast, ...state.toasts].slice(0, TOAST_LIMIT),
      };

    case 'UPDATE_TOAST':
      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === action.toast.id ? { ...t, ...action.toast } : t
        ),
      };

    case 'DISMISS_TOAST': {
      const { toastId } = action;

      // ! Side effects ! - This could be extracted into a dismissToast() action,
      // but I'll keep it here for simplicity
      if (toastId) {
        addToRemoveQueue(toastId);
      } else {
        state.toasts.forEach((toast) => {
          addToRemoveQueue(toast.id);
        });
      }

      return {
        ...state,
        toasts: state.toasts.map((t) =>
          t.id === toastId || toastId === undefined
            ? {
                ...t,
                open: false,
              }
            : t
        ),
      };
    }
    case 'REMOVE_TOAST':
      if (action.toastId === undefined) {
        return {
          ...state,
          toasts: [],
        };
      }
      return {
        ...state,
        toasts: state.toasts.filter((t) => t.id !== action.toastId),
      };
  }
};

const listeners: Array<(state: State) => void> = [];

let memoryState: State = { toasts: [] };

function dispatch(action: Action) {
  memoryState = reducer(memoryState, action);
  listeners.forEach((listener) => {
    listener(memoryState);
  });
}

type Toast = Omit<ToasterToast, 'id'>;

function toast({ ...props }: Toast) {
  const id = genId();

  const update = (props: ToasterToast) =>
    dispatch({
      type: 'UPDATE_TOAST',
      toast: { ...props, id },
    });
  const dismiss = () => dispatch({ type: 'DISMISS_TOAST', toastId: id });

  dispatch({
    type: 'ADD_TOAST',
    toast: {
      ...props,
      id,
      open: true,
      onOpenChange: (open) => {
        if (!open) dismiss();
      },
    },
  });

  return {
    id: id,
    dismiss,
    update,
  };
}

function useToast() {
  const [state, setState] = React.useState<State>(memoryState);

  React.useEffect(() => {
    listeners.push(setState);
    return () => {
      const index = listeners.indexOf(setState);
      if (index > -1) {
        listeners.splice(index, 1);
      }
    };
  }, [state]);

  return {
    ...state,
    toast,
    dismiss: (toastId?: string) => dispatch({ type: 'DISMISS_TOAST', toastId }),
  };
}

export { useToast, toast };

hooks/useCart.tsx.content:
'use client';

import { createContext, useContext, useReducer, ReactNode } from 'react';
import { toast } from 'sonner';
import type { CartItem, Product, ProductVariant } from '@/lib/types';

interface CartState {
  items: CartItem[];
  total: number;
  itemCount: number;
}

type CartAction =
  | { type: 'ADD_ITEM'; payload: { product: Product; variant?: ProductVariant; quantity?: number } }
  | { type: 'REMOVE_ITEM'; payload: string } // product id
  | { type: 'UPDATE_QUANTITY'; payload: { productId: string; quantity: number } }
  | { type: 'CLEAR_CART' }
  | { type: 'LOAD_CART'; payload: CartItem[] };

const CartContext = createContext<{
  state: CartState;
  addItem: (product: Product, variant?: ProductVariant, quantity?: number) => void;
  removeItem: (productId: string) => void;
  updateQuantity: (productId: string, quantity: number) => void;
  clearCart: () => void;
  total: number;
  itemCount: number;
  items: CartItem[];
} | null>(null);

function cartReducer(state: CartState, action: CartAction): CartState {
  switch (action.type) {
    case 'ADD_ITEM': {
      const { product, variant, quantity = 1 } = action.payload;
      const itemKey = `${product._id}-${variant?.value || 'default'}`;

      const existingItemIndex = state.items.findIndex(
        item => `${item.productId}-${item.variant?.value || 'default'}` === itemKey
      );

      let newItems: CartItem[];

      if (existingItemIndex >= 0) {
        // Update existing item quantity
        newItems = state.items.map((item, index) =>
          index === existingItemIndex
            ? { ...item, quantity: item.quantity + quantity }
            : item
        );
      } else {
        // Add new item
        const price = variant?.price || product.price;
        newItems = [...state.items, {
          productId: product._id,
          product,
          variant,
          quantity,
          price,
        }];
      }

      const total = newItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
      const itemCount = newItems.reduce((sum, item) => sum + item.quantity, 0);

      return { items: newItems, total, itemCount };
    }

    case 'REMOVE_ITEM': {
      const newItems = state.items.filter(item => item.productId !== action.payload);
      const total = newItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
      const itemCount = newItems.reduce((sum, item) => sum + item.quantity, 0);

      return { items: newItems, total, itemCount };
    }

    case 'UPDATE_QUANTITY': {
      const { productId, quantity } = action.payload;

      if (quantity <= 0) {
        return cartReducer(state, { type: 'REMOVE_ITEM', payload: productId });
      }

      const newItems = state.items.map(item =>
        item.productId === productId
          ? { ...item, quantity }
          : item
      );

      const total = newItems.reduce((sum, item) => sum + (item.price * item.quantity), 0);
      const itemCount = newItems.reduce((sum, item) => sum + item.quantity, 0);

      return { items: newItems, total, itemCount };
    }

    case 'CLEAR_CART':
      return { items: [], total: 0, itemCount: 0 };

    case 'LOAD_CART': {
      const items = action.payload;
      const total = items.reduce((sum, item) => sum + (item.price * item.quantity), 0);
      const itemCount = items.reduce((sum, item) => sum + item.quantity, 0);

      return { items, total, itemCount };
    }

    default:
      return state;
  }
}

export function CartProvider({ children }: { children: ReactNode }) {
  const [state, dispatch] = useReducer(cartReducer, {
    items: [],
    total: 0,
    itemCount: 0,
  });

  const addItem = (product: Product, variant?: ProductVariant, quantity = 1) => {
    dispatch({ type: 'ADD_ITEM', payload: { product, variant, quantity } });
    toast.success(`Added ${product.title} to cart`);
  };

  const removeItem = (productId: string) => {
    const item = state.items.find(item => item.productId === productId);
    dispatch({ type: 'REMOVE_ITEM', payload: productId });
    if (item) {
      toast.success(`Removed ${item.product.title} from cart`);
    }
  };

  const updateQuantity = (productId: string, quantity: number) => {
    dispatch({ type: 'UPDATE_QUANTITY', payload: { productId, quantity } });
  };

  const clearCart = () => {
    dispatch({ type: 'CLEAR_CART' });
    toast.success('Cart cleared');
  };

  return (
    <CartContext.Provider value={{
      state,
      addItem,
      removeItem,
      updateQuantity,
      clearCart,
      total: state.total,
      itemCount: state.itemCount,
      items: state.items,
    }}>
      {children}
    </CartContext.Provider>
  );
}

export function useCart() {
  const context = useContext(CartContext);
  if (!context) {
    throw new Error('useCart must be used within a CartProvider');
  }
  return context;
}

lib/mock-db.ts.content:
// Mock Database for Development - mimics MongoDB/Mongoose interface
// This allows existing API routes to work without changing code

interface MockDocument {
  _id: string;
  createdAt: Date;
  updatedAt: Date;
  save(): Promise<MockDocument>;
  toJSON(): any;
  toObject(): any;
}

class MockQuery {
  private results: any[] = [];
  private queryObj: any = {};
  private sortObj: any = {};
  private skipNum: number = 0;
  private limitNum: number = 0;
  private selectFields: string = '';
  private isLean: boolean = false;

  constructor(private model: MockModel, private data: Map<string, any>) {}

  find(query: any = {}): this {
    this.queryObj = query;
    return this;
  }

  sort(sortObj: any): this {
    this.sortObj = sortObj;
    return this;
  }

  skip(num: number): this {
    this.skipNum = num;
    return this;
  }

  limit(num: number): this {
    this.limitNum = num;
    return this;
  }

  select(fields: string): this {
    this.selectFields = fields;
    return this;
  }

  lean(): this {
    this.isLean = true;
    return this;
  }

  async exec(): Promise<any[]> {
    // Apply query filtering
    const results: any[] = [];
    for (const [id, doc] of this.data) {
      if (this.model.matchesQuery(doc, this.queryObj)) {
        results.push(this.isLean ? doc : this.model.createDocument(doc));
      }
    }

    // Apply sorting
    if (Object.keys(this.sortObj).length > 0) {
      results.sort((a, b) => {
        for (const [field, direction] of Object.entries(this.sortObj)) {
          const aVal = a[field];
          const bVal = b[field];
          const mult = direction === 1 ? 1 : -1;

          if (aVal < bVal) return -1 * mult;
          if (aVal > bVal) return 1 * mult;
        }
        return 0;
      });
    }

    // Apply skip and limit
    const startIndex = this.skipNum;
    const endIndex = this.limitNum > 0 ? startIndex + this.limitNum : results.length;

    return results.slice(startIndex, endIndex);
  }

  // Make it thenable so it works with await
  then(onfulfilled?: any, onrejected?: any) {
    return this.exec().then(onfulfilled, onrejected);
  }
}

class MockModel {
  private data: Map<string, any> = new Map();
  private idCounter = 1;

  constructor(private name: string, private schema: any) {}

  private generateId(): string {
    return `mock_${this.name}_${this.idCounter++}`;
  }

  find(query: any = {}): MockQuery {
    return new MockQuery(this, this.data).find(query);
  }

  async findOne(query: any): Promise<any | null> {
    for (const [id, doc] of this.data) {
      if (this.matchesQuery(doc, query)) {
        return this.createDocument(doc);
      }
    }
    return null;
  }

  async findById(id: string): Promise<any | null> {
    const doc = this.data.get(id);
    return doc ? this.createDocument(doc) : null;
  }

  async create(docData: any): Promise<any> {
    const id = this.generateId();
    const now = new Date();
    const doc = {
      ...docData,
      _id: id,
      createdAt: now,
      updatedAt: now
    };

    // Generate slug if needed for products
    if (this.name === 'Product' && !doc.slug && doc.title) {
      doc.slug = doc.title
        .toLowerCase()
        .replace(/[^a-z0-9]+/g, '-')
        .replace(/^-|-$/g, '');
    }

    this.data.set(id, doc);
    return this.createDocument(doc);
  }

  async countDocuments(query: any = {}): Promise<number> {
    let count = 0;
    for (const [id, doc] of this.data) {
      if (this.matchesQuery(doc, query)) {
        count++;
      }
    }
    return count;
  }

  matchesQuery(doc: any, query: any): boolean {
    if (!query || Object.keys(query).length === 0) return true;

    for (const [key, value] of Object.entries(query)) {
      if (key === 'price' && typeof value === 'object') {
        // Handle price range queries
        const priceQuery = value as any;
        if (priceQuery.$gte !== undefined && doc.price < priceQuery.$gte) return false;
        if (priceQuery.$lte !== undefined && doc.price > priceQuery.$lte) return false;
      } else if (key === 'tags' && typeof value === 'object') {
        // Handle tags $in queries
        const tagsQuery = value as any;
        if (tagsQuery.$in && !tagsQuery.$in.some((tag: string) => doc.tags?.includes(tag))) {
          return false;
        }
      } else if (doc[key] !== value) {
        return false;
      }
    }
    return true;
  }

  createDocument(data: any): MockDocument {
    const doc = {
      ...data,
      save: async () => {
        data.updatedAt = new Date();
        this.data.set(data._id, data);
        return doc;
      },
      toJSON: () => ({ ...data }),
      toObject: () => ({ ...data })
    } as MockDocument;
    return doc;
  }

  // Seed with initial data
  async seed(initialData: any[]) {
    for (const item of initialData) {
      await this.create(item);
    }
  }
}

// Mock database connection
export class MockDatabase {
  private models: Map<string, MockModel> = new Map();
  private connected = false;

  async connect(): Promise<void> {
    if (this.connected) return;

    console.log('🔄 Connecting to mock database...');

    // Initialize models
