      transition={{ duration: 0.6 }}
      className="container mx-auto px-4 py-8"
    >
      <div className="grid grid-cols-1 lg:grid-cols-2 gap-12 mb-16">
        {/* 3D Product Viewer */}
        <div className="relative">
          <ProductViewer3D 
            product={product} 
            selectedVariant={selectedVariant}
          />
