components/cart/CartItems.tsx.content:
'use client';

import { motion, AnimatePresence } from 'framer-motion';
import { useCart } from '@/hooks/useCart';
import { Minus, Plus, Trash2, Package } from 'lucide-react';
import Image from 'next/image';

export function CartItems() {
  const { items, updateQuantity, removeItem } = useCart();

  if (items.length === 0) {
    return (
      <div className="text-center py-12">
        <Package className="w-16 h-16 text-white/30 mx-auto mb-4" />
        <p className="text-white/70 text-lg mb-2">Your cart is empty</p>
        <p className="text-white/50">Add some premium streetwear to get started</p>
      </div>
    );
  }

  return (
    <div className="space-y-4">
      <AnimatePresence>
        {items.map((item, index) => {
          const itemKey = `${item.productId}-${item.variant?.value || 'default'}-${index}`;
          return (
            <motion.div
              key={itemKey}
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              exit={{ opacity: 0, x: -300 }}
              className="glass-card p-4"
            >
              <div className="flex items-center gap-4">
                {/* Product Image */}
                <div className="relative w-20 h-20 rounded-lg overflow-hidden bg-white/10">
                  {item.product.images && item.product.images.length > 0 ? (
                    <Image
                      src={item.product.images[0]}
                      alt={item.product.title}
                      fill
                      className="object-cover"
                    />
                  ) : (
                    <div className="w-full h-full flex items-center justify-center">
                      <Package className="w-8 h-8 text-white/50" />
                    </div>
                  )}
                </div>

                {/* Product Details */}
                <div className="flex-1">
                  <h3 className="text-white font-semibold">{item.product.title}</h3>
                  <div className="flex items-center gap-2 text-sm text-white/70 mb-2">
                    {item.variant && (
                      <span className="px-2 py-1 bg-white/10 rounded">
                        {item.variant.name}: {item.variant.value}
                      </span>
                    )}
                  </div>
                  <p className="text-brand-auraRed font-bold">${item.price.toFixed(2)}</p>
                </div>

                {/* Quantity Controls */}
                <div className="flex items-center gap-2">
                  <motion.button
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    onClick={() => updateQuantity(item.productId, item.quantity - 1)}
                    className="w-8 h-8 rounded-full bg-white/10 hover:bg-white/20 flex items-center justify-center transition-colors"
                    disabled={item.quantity <= 1}
                  >
                    <Minus className="w-4 h-4 text-white" />
                  </motion.button>

                  <span className="w-8 text-center text-white font-medium">{item.quantity}</span>

                  <motion.button
                    whileHover={{ scale: 1.1 }}
                    whileTap={{ scale: 0.9 }}
                    onClick={() => updateQuantity(item.productId, item.quantity + 1)}
                    className="w-8 h-8 rounded-full bg-white/10 hover:bg-white/20 flex items-center justify-center transition-colors"
                  >
                    <Plus className="w-4 h-4 text-white" />
                  </motion.button>
                </div>

                {/* Remove Button */}
                <motion.button
                  whileHover={{ scale: 1.1 }}
                  whileTap={{ scale: 0.9 }}
                  onClick={() => removeItem(item.productId)}
                  className="w-10 h-10 rounded-lg bg-red-500/20 hover:bg-red-500/30 border border-red-500/30 hover:border-red-500/50 flex items-center justify-center transition-colors"
                >
                  <Trash2 className="w-4 h-4 text-red-400" />
                </motion.button>
              </div>

              {/* Item Total */}
              <div className="flex justify-end mt-3 pt-3 border-t border-white/10">
                <p className="text-white/70">
                  <span className="text-white font-semibold">
                    ${(item.price * item.quantity).toFixed(2)}
                  </span>
                </p>
              </div>
            </motion.div>
          );
        })}
      </AnimatePresence>
    </div>
  );
}

components/checkout/CheckoutForm.tsx.content:
'use client';

import { useState } from 'react';
import { useSession } from 'next-auth/react';
import { motion } from 'framer-motion';
import { useCart } from '@/hooks/useCart';
import { toast } from 'sonner';
import { CreditCard, MapPin, User, Mail, Lock } from 'lucide-react';
import type { Address } from '@/lib/types';

export function CheckoutForm() {
  const { data: session } = useSession();
  const { items, total, clearCart } = useCart();
  const [loading, setLoading] = useState(false);

  const [shippingAddress, setShippingAddress] = useState<Address>({
    name: session?.user?.name || '',
    street: '',
    city: '',
    state: '',
    zipCode: '',
    country: 'US',
  });

  const [paymentMethod, setPaymentMethod] = useState('card');

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLSelectElement>) => {
    const { name, value } = e.target;
    setShippingAddress(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const validateForm = () => {
    const required = ['name', 'street', 'city', 'state', 'zipCode'];
    const missing = required.filter(field => !shippingAddress[field as keyof Address]);

    if (missing.length > 0) {
      toast.error(`Please fill in: ${missing.join(', ')}`);
      return false;
    }

    if (items.length === 0) {
      toast.error('Your cart is empty');
      return false;
    }

    return true;
  };

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();

    if (!validateForm()) return;

    setLoading(true);

    try {
      // Create order
      const orderData = {
        items: items.map(item => ({
          productId: item.productId,
          product: item.product,
          variant: item.variant,
          quantity: item.quantity,
          price: item.price
        })),
        total,
        shippingAddress
      };

      const response = await fetch('/api/orders', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(orderData),
      });

      const data = await response.json();

      if (response.ok && data.success) {
        toast.success('Order placed successfully!');
        clearCart();

        // For now, just show success - in real app would integrate Stripe
        setTimeout(() => {
          window.location.href = `/orders/${data.order._id}?success=true`;
        }, 1000);
      } else {
        toast.error(data.error || 'Failed to place order');
      }
    } catch (error) {
      console.error('Checkout error:', error);
      toast.error('Something went wrong. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <form onSubmit={handleSubmit} className="space-y-8">
      {/* Shipping Information */}
      <div className="glass-card p-6">
        <div className="flex items-center gap-3 mb-6">
          <div className="w-10 h-10 bg-brand-auraRed/20 rounded-lg flex items-center justify-center">
            <MapPin className="w-5 h-5 text-brand-auraRed" />
          </div>
          <h2 className="text-xl font-bold text-white">Shipping Information</h2>
        </div>

        <div className="grid md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-white/90 mb-2">
              Full Name *
            </label>
            <div className="relative">
              <User className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/50 w-5 h-5" />
              <input
                type="text"
                name="name"
                required
                value={shippingAddress.name}
                onChange={handleInputChange}
                className="w-full pl-10 pr-4 py-3 bg-white/5 border border-white/20 rounded-lg text-white
                         placeholder:text-white/50 focus:outline-none focus:border-brand-auraRed/50"
                placeholder="Enter your full name"
              />
            </div>
          </div>

          <div>
            <label className="block text-sm font-medium text-white/90 mb-2">
              Email Address
            </label>
            <div className="relative">
              <Mail className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/50 w-5 h-5" />
              <input
                type="email"
                value={session?.user?.email || ''}
                disabled
                className="w-full pl-10 pr-4 py-3 bg-white/5 border border-white/20 rounded-lg text-white/70
                         placeholder:text-white/50 cursor-not-allowed"
                placeholder="Email address"
              />
            </div>
          </div>
        </div>

        <div className="mt-4">
          <label className="block text-sm font-medium text-white/90 mb-2">
            Street Address *
          </label>
          <input
            type="text"
            name="street"
            required
            value={shippingAddress.street}
            onChange={handleInputChange}
            className="w-full px-4 py-3 bg-white/5 border border-white/20 rounded-lg text-white
                     placeholder:text-white/50 focus:outline-none focus:border-brand-auraRed/50"
            placeholder="Enter your street address"
          />
        </div>

        <div className="grid md:grid-cols-3 gap-4 mt-4">
          <div>
            <label className="block text-sm font-medium text-white/90 mb-2">
              City *
            </label>
            <input
              type="text"
              name="city"
              required
              value={shippingAddress.city}
              onChange={handleInputChange}
              className="w-full px-4 py-3 bg-white/5 border border-white/20 rounded-lg text-white
                       placeholder:text-white/50 focus:outline-none focus:border-brand-auraRed/50"
              placeholder="City"
            />
          </div>

          <div>
            <label className="block text-sm font-medium text-white/90 mb-2">
              State *
            </label>
            <select
              name="state"
              required
              value={shippingAddress.state}
              onChange={handleInputChange}
              className="w-full px-4 py-3 bg-white/5 border border-white/20 rounded-lg text-white
                       focus:outline-none focus:border-brand-auraRed/50"
            >
              <option value="" className="bg-brand-black">Select State</option>
              <option value="CA" className="bg-brand-black">California</option>
              <option value="NY" className="bg-brand-black">New York</option>
              <option value="TX" className="bg-brand-black">Texas</option>
              <option value="FL" className="bg-brand-black">Florida</option>
              {/* Add more states as needed */}
            </select>
          </div>

          <div>
            <label className="block text-sm font-medium text-white/90 mb-2">
              ZIP Code *
            </label>
            <input
              type="text"
              name="zipCode"
              required
              value={shippingAddress.zipCode}
              onChange={handleInputChange}
              className="w-full px-4 py-3 bg-white/5 border border-white/20 rounded-lg text-white
                       placeholder:text-white/50 focus:outline-none focus:border-brand-auraRed/50"
              placeholder="12345"
            />
          </div>
        </div>
      </div>

      {/* Payment Method */}
      <div className="glass-card p-6">
        <div className="flex items-center gap-3 mb-6">
          <div className="w-10 h-10 bg-green-500/20 rounded-lg flex items-center justify-center">
            <CreditCard className="w-5 h-5 text-green-400" />
          </div>
          <h2 className="text-xl font-bold text-white">Payment Method</h2>
        </div>

        <div className="space-y-4">
          <div className="flex items-center gap-3 p-4 bg-white/5 rounded-lg border border-white/20">
            <input
              type="radio"
              id="card"
              name="paymentMethod"
              value="card"
              checked={paymentMethod === 'card'}
              onChange={(e) => setPaymentMethod(e.target.value)}
              className="text-brand-auraRed focus:ring-brand-auraRed"
            />
            <label htmlFor="card" className="text-white font-medium">Credit Card</label>
          </div>

          {/* Placeholder for Stripe integration */}
          <div className="p-4 bg-blue-500/10 border border-blue-500/30 rounded-lg">
            <div className="flex items-center gap-2 text-blue-400 text-sm mb-2">
              <Lock className="w-4 h-4" />
              <span>Secure Payment with Stripe</span>
            </div>
            <p className="text-white/70 text-sm">
              Payment processing will be integrated with Stripe for secure transactions.
              For demo purposes, clicking "Place Order" will create the order without payment.
            </p>
          </div>
        </div>
      </div>

      {/* Place Order Button */}
      <motion.button
        type="submit"
        disabled={loading}
        whileHover={{ scale: loading ? 1 : 1.02 }}
        whileTap={{ scale: loading ? 1 : 0.98 }}
        className="w-full py-4 bg-aura-gradient text-white font-bold text-lg rounded-lg
                 hover:shadow-lg hover:shadow-brand-auraRed/25 transition-all duration-200
                 disabled:opacity-50 disabled:cursor-not-allowed
                 flex items-center justify-center gap-2"
      >
        {loading ? (
          <>
            <div className="animate-spin w-5 h-5 border-2 border-white/30 border-t-white rounded-full" />
            Processing Order...
          </>
        ) : (
          <>
            <CreditCard className="w-5 h-5" />
            Place Order - ${total.toFixed(2)}
          </>
        )}
      </motion.button>
    </form>
  );
}

components/checkout/OrderSummary.tsx.content:
'use client';

import { motion } from 'framer-motion';
import { useCart } from '@/hooks/useCart';
import { Package, Truck, Shield } from 'lucide-react';
import Image from 'next/image';

export function OrderSummary() {
  const { items, total } = useCart();

  const subtotal = total;
  const shipping = subtotal > 100 ? 0 : 9.99;
  const tax = subtotal * 0.08; // 8% tax
  const finalTotal = subtotal + shipping + tax;

  return (
    <div className="space-y-6">
      {/* Order Items */}
      <div className="glass-card p-6">
        <h2 className="text-xl font-bold text-white mb-6">Order Summary</h2>

        <div className="space-y-4 mb-6">
          {items.map((item, index) => (
            <motion.div
              key={`${item.productId}-${item.variant?.value || 'default'}`}
              initial={{ opacity: 0, y: 10 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ delay: index * 0.1 }}
              className="flex items-center gap-4"
            >
              {/* Product Image */}
              <div className="relative w-16 h-16 rounded-lg overflow-hidden bg-white/10 flex-shrink-0">
                {item.product.images && item.product.images.length > 0 ? (
                  <Image
                    src={item.product.images[0]}
                    alt={item.product.title}
                    fill
                    className="object-cover"
                  />
                ) : (
                  <div className="w-full h-full flex items-center justify-center">
                    <Package className="w-6 h-6 text-white/50" />
                  </div>
                )}

                {/* Quantity Badge */}
                <div className="absolute -top-2 -right-2 w-6 h-6 bg-brand-auraRed rounded-full flex items-center justify-center text-white text-xs font-bold">
                  {item.quantity}
                </div>
              </div>

              {/* Product Info */}
              <div className="flex-1 min-w-0">
                <h3 className="text-white font-medium truncate">{item.product.title}</h3>
                {item.variant && (
                  <p className="text-white/70 text-sm">
                    {item.variant.name}: {item.variant.value}
                  </p>
                )}
                <p className="text-white/50 text-sm">
                  ${item.price.toFixed(2)} × {item.quantity}
                </p>
              </div>

              {/* Item Total */}
              <div className="text-right">
                <p className="text-white font-semibold">
                  ${(item.price * item.quantity).toFixed(2)}
                </p>
              </div>
            </motion.div>
          ))}
        </div>

        {/* Pricing Breakdown */}
        <div className="border-t border-white/10 pt-4 space-y-3">
          <div className="flex justify-between text-white/70">
            <span>Subtotal ({items.length} item{items.length > 1 ? 's' : ''})</span>
            <span>${subtotal.toFixed(2)}</span>
          </div>

          <div className="flex justify-between text-white/70">
            <span>Shipping</span>
            <span className={shipping === 0 ? 'text-green-400' : ''}>
              {shipping === 0 ? 'FREE' : `$${shipping.toFixed(2)}`}
            </span>
          </div>

          <div className="flex justify-between text-white/70">
            <span>Tax</span>
            <span>${tax.toFixed(2)}</span>
          </div>

          <div className="border-t border-white/10 pt-3">
            <div className="flex justify-between text-white font-bold text-lg">
              <span>Total</span>
              <span>${finalTotal.toFixed(2)}</span>
            </div>
          </div>
        </div>

        {/* Free Shipping Notice */}
        {subtotal < 100 && (
          <div className="mt-4 p-3 bg-blue-500/20 border border-blue-500/30 rounded-lg">
            <div className="flex items-center gap-2 text-blue-400 text-sm">
              <Truck className="w-4 h-4" />
              <span>
                Add ${(100 - subtotal).toFixed(2)} more for FREE shipping!
              </span>
            </div>
          </div>
        )}
      </div>

      {/* Security & Guarantees */}
      <div className="glass-card p-6">
        <h3 className="text-lg font-bold text-white mb-4">Order Protection</h3>

        <div className="space-y-4">
          <div className="flex items-center gap-3">
            <Shield className="w-5 h-5 text-green-400 flex-shrink-0" />
            <div>
              <div className="text-white font-medium">Secure Checkout</div>
              <div className="text-white/70 text-sm">SSL encrypted and secure</div>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <Truck className="w-5 h-5 text-blue-400 flex-shrink-0" />
            <div>
              <div className="text-white font-medium">Fast Delivery</div>
              <div className="text-white/70 text-sm">2-5 business days</div>
            </div>
          </div>

          <div className="flex items-center gap-3">
            <Package className="w-5 h-5 text-purple-400 flex-shrink-0" />
            <div>
              <div className="text-white font-medium">Quality Guaranteed</div>
              <div className="text-white/70 text-sm">Premium streetwear only</div>
            </div>
          </div>
        </div>
      </div>

      {/* Support Info */}
      <div className="text-center text-white/50 text-sm">
        <p>Need help? Contact support@ouswear.com</p>
        <p className="mt-1">🔒 Your payment information is secure</p>
      </div>
    </div>
  );
}

components/order/OrderList.tsx.content:
'use client';

import { useState, useEffect } from 'react';
import { motion, AnimatePresence } from 'framer-motion';
import { Package, Calendar, DollarSign, Truck, Filter, Search } from 'lucide-react';
import { OrderSummary } from './OrderSummary';
import type { Order } from '@/lib/types';

interface OrderListProps {
  userId?: string; // For customer view
  isAdmin?: boolean; // For admin view
}

export function OrderList({ userId, isAdmin = false }: OrderListProps) {
  const [orders, setOrders] = useState<Order[]>([]);
  const [loading, setLoading] = useState(true);
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null);
  const [statusFilter, setStatusFilter] = useState<string>('');
  const [searchTerm, setSearchTerm] = useState('');

  useEffect(() => {
    fetchOrders();
  }, [userId, statusFilter]);

  const fetchOrders = async () => {
    try {
      setLoading(true);

      let url = '/api/orders';
      if (userId) {
        url += `?userId=${userId}`;
      }
      if (statusFilter) {
        url += `${userId ? '&' : '?'}status=${statusFilter}`;
      }

      const response = await fetch(url);
      if (response.ok) {
        const data = await response.json();
        if (data.success) {
          setOrders(data.orders || []);
        }
      }
    } catch (error) {
      console.error('Failed to fetch orders:', error);
    } finally {
      setLoading(false);
    }
  };

  const filteredOrders = orders.filter(order => {
    if (!searchTerm) return true;
    const searchLower = searchTerm.toLowerCase();
    return (
      order._id.toLowerCase().includes(searchLower) ||
      order.items.some(item => 
        item.product?.title.toLowerCase().includes(searchLower)
      )
    );
  });

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30';
      case 'confirmed':
        return 'bg-blue-500/20 text-blue-400 border-blue-500/30';
      case 'shipped':
        return 'bg-purple-500/20 text-purple-400 border-purple-500/30';
      case 'delivered':
        return 'bg-green-500/20 text-green-400 border-green-500/30';
      case 'canceled':
        return 'bg-red-500/20 text-red-400 border-red-500/30';
      default:
        return 'bg-white/10 text-white/70 border-white/20';
    }
  };

  if (loading) {
    return (
      <div className="space-y-4">
        {[...Array(3)].map((_, i) => (
          <div key={i} className="animate-pulse glass-card p-6">
            <div className="flex items-center justify-between mb-4">
              <div className="space-y-2">
                <div className="h-5 bg-white/10 rounded w-32" />
                <div className="h-4 bg-white/10 rounded w-24" />
              </div>
              <div className="h-8 bg-white/10 rounded w-20" />
            </div>
            <div className="space-y-3">
              <div className="h-4 bg-white/10 rounded" />
              <div className="h-4 bg-white/10 rounded w-3/4" />
            </div>
          </div>
        ))}
      </div>
    );
  }

  return (
    <div className="space-y-6">
      {/* Filters and Search */}
      <div className="glass-card p-4">
        <div className="flex flex-col md:flex-row gap-4">
          {/* Search */}
          <div className="relative flex-1">
            <Search className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/50 w-5 h-5" />
            <input
              type="text"
              placeholder="Search orders..."
              value={searchTerm}
              onChange={(e) => setSearchTerm(e.target.value)}
              className="w-full pl-10 pr-4 py-2 bg-white/10 border border-white/20 rounded-lg text-white
                        placeholder:text-white/50 focus:outline-none focus:border-brand-auraRed/50"
            />
          </div>

          {/* Status Filter */}
          <div className="relative">
            <Filter className="absolute left-3 top-1/2 transform -translate-y-1/2 text-white/50 w-5 h-5" />
            <select
              value={statusFilter}
              onChange={(e) => setStatusFilter(e.target.value)}
              className="pl-10 pr-8 py-2 bg-white/10 border border-white/20 rounded-lg text-white
                        focus:outline-none focus:border-brand-auraRed/50 appearance-none min-w-[150px]"
            >
              <option value="" className="bg-brand-black">All Status</option>
              <option value="pending" className="bg-brand-black">Pending</option>
              <option value="confirmed" className="bg-brand-black">Confirmed</option>
              <option value="shipped" className="bg-brand-black">Shipped</option>
              <option value="delivered" className="bg-brand-black">Delivered</option>
              <option value="canceled" className="bg-brand-black">Canceled</option>
            </select>
          </div>
        </div>
      </div>

      {/* Orders List */}
      {filteredOrders.length === 0 ? (
        <div className="glass-card p-12 text-center">
          <Package className="w-16 h-16 text-white/30 mx-auto mb-4" />
          <p className="text-white/70 text-lg mb-2">No orders found</p>
          <p className="text-white/50">
            {statusFilter || searchTerm 
              ? "Try adjusting your filters or search terms"
              : "Your order history will appear here once you make a purchase"
            }
          </p>
        </div>
      ) : (
        <div className="space-y-4">
          <AnimatePresence>
            {filteredOrders.map((order) => (
              <motion.div
                key={order._id}
                initial={{ opacity: 0, y: 20 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -20 }}
                className="glass-card p-4 cursor-pointer hover:bg-white/5 transition-colors"
                onClick={() => setSelectedOrder(order)}
              >
                <div className="flex items-center justify-between">
                  <div className="flex items-center gap-4">
                    <div className="w-12 h-12 bg-aura-gradient rounded-lg flex items-center justify-center">
                      <Package className="w-6 h-6 text-white" />
                    </div>
                    <div>
                      <h3 className="text-white font-semibold">
                        Order #{order._id.slice(-6)}
                      </h3>
                      <div className="flex items-center gap-4 text-white/70 text-sm">
                        <div className="flex items-center gap-1">
                          <Calendar className="w-4 h-4" />
                          {new Date(order.createdAt).toLocaleDateString()}
                        </div>
                        <div className="flex items-center gap-1">
                          <Package className="w-4 h-4" />
                          {order.items.length} item{order.items.length > 1 ? 's' : ''}
                        </div>
                      </div>
                    </div>
                  </div>

                  <div className="flex items-center gap-4">
                    <div className="text-right">
                      <div className="flex items-center gap-1 text-white font-semibold">
                        <DollarSign className="w-4 h-4" />
                        ${order.total.toFixed(2)}
                      </div>
                      {order.tracking && (
                        <div className="flex items-center gap-1 text-white/70 text-sm">
                          <Truck className="w-3 h-3" />
                          <span>Tracking: {order.tracking.slice(-6)}</span>
                        </div>
                      )}
                    </div>

                    <span className={`px-3 py-1 rounded-full text-sm font-medium border ${getStatusColor(order.status)}`}>
                      {order.status.charAt(0).toUpperCase() + order.status.slice(1)}
                    </span>
                  </div>
                </div>
              </motion.div>
            ))}
          </AnimatePresence>
        </div>
      )}

      {/* Order Detail Modal */}
      <AnimatePresence>
        {selectedOrder && (
          <motion.div
            initial={{ opacity: 0 }}
            animate={{ opacity: 1 }}
            exit={{ opacity: 0 }}
            className="fixed inset-0 bg-black/80 backdrop-blur-sm z-50 flex items-center justify-center p-4"
            onClick={() => setSelectedOrder(null)}
          >
            <motion.div
              initial={{ scale: 0.9, opacity: 0 }}
              animate={{ scale: 1, opacity: 1 }}
              exit={{ scale: 0.9, opacity: 0 }}
              className="max-w-2xl w-full max-h-[90vh] overflow-y-auto"
              onClick={(e) => e.stopPropagation()}
            >
              <OrderSummary order={selectedOrder} />
              <div className="mt-4 text-center">
                <button
                  onClick={() => setSelectedOrder(null)}
                  className="px-6 py-2 border border-white/20 text-white/70 hover:text-white 
                           hover:border-white/40 rounded-lg transition-all duration-200"
                >
                  Close
                </button>
              </div>
            </motion.div>
          </motion.div>
        )}
      </AnimatePresence>
    </div>
  );
}

components/order/OrderSummary.tsx.content:
'use client';

import { motion } from 'framer-motion';
import { Calendar, Package, Truck, CheckCircle, Clock, MapPin } from 'lucide-react';
import type { Order } from '@/lib/types';

interface OrderSummaryProps {
  order: Order;
}

export function OrderSummary({ order }: OrderSummaryProps) {
  const getStatusIcon = (status: string) => {
    switch (status) {
      case 'pending':
        return <Clock className="w-5 h-5 text-yellow-400" />;
      case 'confirmed':
        return <CheckCircle className="w-5 h-5 text-blue-400" />;
      case 'shipped':
        return <Truck className="w-5 h-5 text-purple-400" />;
      case 'delivered':
        return <CheckCircle className="w-5 h-5 text-green-400" />;
      default:
        return <Package className="w-5 h-5 text-white/50" />;
    }
  };

  const getStatusColor = (status: string) => {
    switch (status) {
      case 'pending':
        return 'bg-yellow-500/20 text-yellow-400 border-yellow-500/30';
      case 'confirmed':
        return 'bg-blue-500/20 text-blue-400 border-blue-500/30';
      case 'shipped':
        return 'bg-purple-500/20 text-purple-400 border-purple-500/30';
      case 'delivered':
        return 'bg-green-500/20 text-green-400 border-green-500/30';
      case 'canceled':
        return 'bg-red-500/20 text-red-400 border-red-500/30';
      default:
        return 'bg-white/10 text-white/70 border-white/20';
    }
  };

  return (
    <motion.div
      initial={{ opacity: 0, y: 20 }}
      animate={{ opacity: 1, y: 0 }}
      className="glass-card p-6"
    >
      {/* Order Header */}
      <div className="flex items-center justify-between mb-6">
        <div>
          <h2 className="text-xl font-bold text-white">Order #{order._id.slice(-6)}</h2>
          <div className="flex items-center gap-2 text-white/70 text-sm mt-1">
            <Calendar className="w-4 h-4" />
            {new Date(order.createdAt).toLocaleDateString('en-US', {
              year: 'numeric',
              month: 'long',
              day: 'numeric',
              hour: '2-digit',
              minute: '2-digit'
            })}
          </div>
        </div>

        <div className="flex items-center gap-3">
          <span className={`px-3 py-1 rounded-full text-sm font-medium border ${getStatusColor(order.status)}`}>
            {getStatusIcon(order.status)}
            <span className="ml-2 capitalize">{order.status}</span>
          </span>
        </div>
      </div>

      {/* Order Items */}
      <div className="space-y-4 mb-6">
        <h3 className="font-semibold text-white">Items Ordered</h3>
        {order.items.map((item, index) => (
          <div key={index} className="flex items-center gap-4 p-3 bg-white/5 rounded-lg">
            <div className="w-16 h-16 bg-white/10 rounded-lg flex items-center justify-center">
              <Package className="w-6 h-6 text-white/50" />
            </div>
            <div className="flex-1">
              <p className="text-white font-medium">
                {item.product?.title || `Product ${item.productId}`}
              </p>
              {item.variant && (
                <p className="text-white/70 text-sm">
                  {item.variant.name}: {item.variant.value}
                </p>
              )}
              <div className="flex items-center gap-2 text-sm text-white/70">
                <span>Qty: {item.quantity}</span>
                <span>•</span>
                <span>${item.price.toFixed(2)} each</span>
              </div>
            </div>
            <div className="text-right">
              <p className="text-white font-semibold">
                ${(item.price * item.quantity).toFixed(2)}
              </p>
            </div>
          </div>
        ))}
      </div>

      {/* Pricing Breakdown */}
      <div className="border-t border-white/10 pt-4 mb-6">
        <div className="space-y-2">
          <div className="flex justify-between text-white/70">
            <span>Subtotal</span>
            <span>${(order.total * 0.926).toFixed(2)}</span> {/* Approximate subtotal */}
          </div>
          <div className="flex justify-between text-white/70">
            <span>Tax</span>
            <span>${(order.total * 0.074).toFixed(2)}</span>
          </div>
          <div className="flex justify-between text-white/70">
            <span>Shipping</span>
            <span>{order.total > 100 ? 'FREE' : '$9.99'}</span>
          </div>
          <div className="flex justify-between text-white font-bold text-lg pt-2 border-t border-white/10">
            <span>Total</span>
            <span>${order.total.toFixed(2)}</span>
          </div>
        </div>
      </div>

      {/* Shipping Address */}
      <div className="border-t border-white/10 pt-4">
        <div className="flex items-start gap-3">
          <MapPin className="w-5 h-5 text-brand-auraRed flex-shrink-0 mt-1" />
          <div>
            <h4 className="font-semibold text-white mb-2">Shipping Address</h4>
            <div className="text-white/70 text-sm space-y-1">
              <p>{order.shippingAddress.name}</p>
              <p>{order.shippingAddress.street}</p>
              <p>
                {order.shippingAddress.city}, {order.shippingAddress.state} {order.shippingAddress.zipCode}
              </p>
              <p>{order.shippingAddress.country}</p>
            </div>
          </div>
        </div>
      </div>

      {/* Tracking Info */}
      {order.tracking && (
        <div className="border-t border-white/10 pt-4 mt-4">
          <div className="flex items-center gap-3">
            <Truck className="w-5 h-5 text-brand-auraRed" />
            <div>
              <p className="text-white font-medium">Tracking Number</p>
              <p className="text-brand-auraRed font-mono text-sm">{order.tracking}</p>
            </div>
          </div>
        </div>
      )}
    </motion.div>
  );
}

components/product/ProductInfo.tsx.content:
'use client';

import { useState } from 'react';
import { motion } from 'framer-motion';
import { useCart } from '@/hooks/useCart';
import { ShoppingBag, Heart, Star, Truck, Shield, RotateCcw } from 'lucide-react';
import { toast } from 'sonner';
import type { Product, ProductVariant } from '@/lib/types';

interface ProductInfoProps {
  product: Product;
}

export function ProductInfo({ product }: ProductInfoProps) {
  const { addItem } = useCart();
  const [selectedVariant, setSelectedVariant] = useState<ProductVariant | undefined>(
    product.variants && product.variants.length > 0 ? product.variants[0] : undefined
  );
  const [quantity, setQuantity] = useState(1);
  const [isFavorited, setIsFavorited] = useState(false);

  const price = selectedVariant?.price || product.price;

  const handleAddToCart = () => {
    addItem(product, selectedVariant, quantity);
    toast.success(`Added ${quantity}x ${product.title} to cart!`);
  };

  const handleFavorite = () => {
    setIsFavorited(!isFavorited);
    toast.success(isFavorited ? 'Removed from favorites' : 'Added to favorites');
  };

  return (
    <div className="space-y-8">
      {/* Product Title and Rating */}
      <div>
        <h1 className="text-3xl md:text-4xl font-heavy text-white mb-4">
          {product.title}
        </h1>

        {/* Rating */}
        <div className="flex items-center gap-4 mb-4">
          <div className="flex items-center gap-1">
            {[...Array(5)].map((_, i) => (
              <Star
                key={i}
                className={`w-5 h-5 ${
                  i < 4 ? 'text-yellow-400 fill-current' : 'text-white/30'
                }`}
              />
            ))}
          </div>
          <span className="text-white/70 text-sm">(42 reviews)</span>
        </div>

        {/* Price */}
        <div className="flex items-center gap-4">
          <span className="text-3xl font-bold text-white">${price.toFixed(2)}</span>
          {selectedVariant?.price && selectedVariant.price !== product.price && (
            <span className="text-lg text-white/50 line-through">
              ${product.price.toFixed(2)}
            </span>
          )}
        </div>
      </div>

      {/* Description */}
      <div className="glass-card p-6">
        <h3 className="text-xl font-bold text-white mb-4">Description</h3>
        <p className="text-white/70 leading-relaxed">
          {product.description}
        </p>
      </div>

      {/* Variants */}
      {product.variants && product.variants.length > 0 && (
        <div className="glass-card p-6">
          <h3 className="text-lg font-bold text-white mb-4">
            {product.variants[0].name}
          </h3>
          <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
            {product.variants.map((variant) => (
              <motion.button
                key={variant._id}
                whileHover={{ scale: 1.02 }}
                whileTap={{ scale: 0.98 }}
                onClick={() => setSelectedVariant(variant)}
                className={`p-3 rounded-lg border transition-all ${
                  selectedVariant?._id === variant._id
                    ? 'border-brand-auraRed bg-brand-auraRed/20 text-white'
                    : 'border-white/20 bg-white/5 text-white/70 hover:border-white/40 hover:text-white'
                }`}
              >
                <span className="font-medium">{variant.value}</span>
                {variant.price && variant.price !== product.price && (
                  <div className="text-sm text-brand-auraRed font-medium">
                    ${variant.price.toFixed(2)}
                  </div>
                )}
              </motion.button>
            ))}
          </div>
        </div>
      )}

      {/* Quantity and Actions */}
      <div className="glass-card p-6">
        <div className="flex items-center justify-between mb-6">
          <div className="flex items-center gap-4">
            <span className="text-white font-medium">Quantity:</span>
            <div className="flex items-center gap-2">
              <motion.button
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={() => setQuantity(Math.max(1, quantity - 1))}
                className="w-10 h-10 rounded-full bg-white/10 hover:bg-white/20 flex items-center justify-center transition-colors"
              >
                <span className="text-white font-bold">−</span>
              </motion.button>
              <span className="w-12 text-center text-white font-bold">{quantity}</span>
              <motion.button
                whileHover={{ scale: 1.1 }}
                whileTap={{ scale: 0.9 }}
                onClick={() => setQuantity(quantity + 1)}
                className="w-10 h-10 rounded-full bg-white/10 hover:bg-white/20 flex items-center justify-center transition-colors"
              >
                <span className="text-white font-bold">+</span>
              </motion.button>
            </div>
          </div>

          <div className="text-right">
            <div className="text-white/70 text-sm">Stock</div>
            <div className="text-white font-bold">{selectedVariant?.stock || product.stock} units</div>
          </div>
        </div>

        {/* Action Buttons */}
        <div className="flex gap-4">
